.echo ON
.timer ON

-- random value as BIGINT to avoid floating point rounding
CREATE OR REPLACE TABLE random_table (
    id BIGINT,
    hash UINT64,
    rand DOUBLE,
    value BIGINT
);

SELECT SETSEED(1);

INSERT INTO random_table
select 
    (i) AS id, 
    hash(i) AS hash, 
    IF (i % 2, true, false) AS value,
    RANDOM()*1E17 as value 
from generate_series(1, 50) s(i) 
order by id;

